def is_number(s):
    try:
        float(s)
        return True
    except ValueError:
        return False


def word_valid(w):
    if w in stop_words:
        return False
    if config.word_count[w] < config.word_freq_min_threshold:
        return False
    if config.word_count[w] > config.word_freq_max_threshold:
        return False
    if w.startswith('re'):
        return False
    if w.startswith('de'):
        return False
    if w.startswith('co'):
        return False
    if is_number(w):
        return False
    return True
